import "core-js/modules/web.dom.iterable";
import AppHead from '@/components/common/AppHeader.vue';
import AppBody from '@/components/common/AppPageBody.vue';
import AppFoot from '@/components/common/AppFoot.vue';
export default {
  name: "shopCart",
  components: {
    AppHead: AppHead,
    AppBody: AppBody,
    AppFoot: AppFoot
  },
  data: function data() {
    return {
      isShowTip: false,
      tipContent: "",
      dataList: [],
      idlePlace: '',
      total: 0,
      idleItemids: [],
      favoriteIds: []
    };
  },
  mounted: function mounted() {},
  created: function created() {
    var _this = this;
    if (!this.$globalData.userInfo.nickname) {
      this.$api.getUserInfo().then(function (res) {
        if (res.status_code === 1) {
          res.data.signInTime = res.data.signInTime.substring(0, 10);
          console.log(res.data);
          _this.$globalData.userInfo = res.data;
          _this.userInfo = _this.$globalData.userInfo;
        }
      });
    } else {
      this.userInfo = this.$globalData.userInfo;
      console.log(this.userInfo);
    }
    this.getMyFavorite();
    this.getIdleItemData();
  },
  computed: {},
  methods: {
    /*支付功能*/payAll: function payAll() {
      var _this2 = this;
      this.$api.addOrderByIds({
        idleItemIds: this.idleItemids,
        total_price: this.total
      }).then(function (res) {
        console.log(res);
        if (res.status_code === 1) {
          _this2.$api.removeShopCartByIds({
            favoriteIds: _this2.favoriteIds
          }).then(function (res) {
            // alert("支付成功，商品已经从购物车中移出")
          });
          _this2.$router.push({
            path: '/order',
            query: {
              id: res.data.id
            }
          });
        } else {
          _this2.$message.error(res.msg);
        }
      }).catch(function (e) {});
    },
    /*多选框*/toggleSelection: function toggleSelection(rows) {
      var _this3 = this;
      if (rows) {
        rows.forEach(function (row) {
          _this3.$refs.multipleTable.toggleRowSelection(row);
        });
      } else {
        this.$refs.multipleTable.clearSelection();
      }
    },
    handleSelectionChange: function handleSelectionChange(val) {
      this.multipleSelection = val;
      console.log(val);

      /*计算勾选总价*/
      if (val.length !== 0) {
        for (var i = 0; i < val.length; i++) {
          this.idleItemids[i] = val[i].id;
          this.favoriteIds[i] = val[i].favoriteId;
        }
        console.log(this.idleItemids, this.favoriteIds);
        this.total = this.countTotal(val, 'idlePrice');
      } else {
        this.total = 0;
      }
    },
    countTotal: function countTotal(arr, keyName) {
      var $total = 0;
      $total = arr.reduce(function (total, currentValue, currentIndex, arr) {
        return currentValue[keyName] ? total + currentValue[keyName] : total;
      }, 0);
      return $total;
    },
    removeShopCart: function removeShopCart(row, index) {
      var _this4 = this;
      this.$api.deleteFavorite({
        id: row.favoriteId
      }).then(function (res) {
        console.log(res);
        console.log(row);
        // console.log("列表的数据"+ row.id);
        if (res.status_code === 1) {
          _this4.$message({
            message: '已取消购物车！',
            type: 'success'
          });
          _this4.dataList.splice(index, 1);
          // this.getMyFavorite()
        } else {
          _this4.$message.error(res.msg);
        }
        // this.getMyFavorite()
      });
    },
    /*获取购物车内容*/getMyFavorite: function getMyFavorite() {
      var _this5 = this;
      this.$api.getMyFavorite().then(function (res) {
        console.log('getMyFavorite', res);
        if (res.status_code === 1) {
          for (var i = 0; i < res.data.length; i++) {
            var pictureList = JSON.parse(res.data[i].idleItem.pictureList);
            _this5.dataList.push({
              favoriteId: res.data[i].id,
              id: res.data[i].idleItem.id,
              imgUrl: pictureList.length > 0 ? pictureList[0] : '',
              idleName: res.data[i].idleItem.idleName,
              idleDetails: res.data[i].idleItem.idleDetails,
              timeStr: res.data[i].createTime.substring(0, 10) + " " + res.data[i].createTime.substring(11, 19),
              idlePrice: res.data[i].idleItem.idlePrice,
              idleStatus: res.data[i].idleItem.idleStatus
            });
            _this5.idlePlace = res.data[i].idleItem.idlePlace;
          }
        }
      });
    }
  }
};
