import "core-js/modules/es6.regexp.split";
import AppHead from "../common/AppHeader.vue";
import AppBody from "../common/AppPageBody.vue";
import AppFoot from "../common/AppFoot.vue";
export default {
  name: "message",
  components: {
    AppHead: AppHead,
    AppBody: AppBody,
    AppFoot: AppFoot
  },
  data: function data() {
    return {
      meslist: []
    };
  },
  created: function created() {
    var _this = this;
    this.$api.getAllMyMessage().then(function (res) {
      console.log(res);
      if (res.status_code === 1) {
        for (var i = 0; i < res.data.length; i++) {
          var imgList = JSON.parse(res.data[i].idle.pictureList);
          res.data[i].idle.imgUrl = imgList ? imgList[0] : '';
          var contentList = res.data[i].content.split('<br>');
          var contenHtml = contentList[0];
          for (var _i = 1; _i < contentList.length; _i++) {
            contenHtml += '  ' + contentList[_i];
          }
          res.data[i].content = contenHtml;
        }
        _this.meslist = res.data;
      }
    });
  },
  methods: {
    toDetails: function toDetails(id) {
      this.$router.push({
        path: '/details',
        query: {
          id: id
        }
      });
    }
  }
};
