import "core-js/modules/es6.regexp.replace";
import AppHead from "../common/AppHeader.vue";
import AppBody from "../common/AppPageBody.vue";
import AppFoot from "../common/AppFoot.vue";
import AppCarousel from '@/components/common/AppCarousel';
export default {
  name: "index",
  components: {
    AppCarousel: AppCarousel,
    AppHead: AppHead,
    AppBody: AppBody,
    AppFoot: AppFoot
  },
  data: function data() {
    return {
      labelName: '0',
      idleList: [],
      currentPage: 1,
      totalItem: 1
    };
  },
  created: function created() {
    this.findIdleTiem(1);
  },
  mounted: function mounted() {},
  watch: {
    $route: function $route(to, from) {
      this.labelName = to.query.labelName;
      var val = parseInt(to.query.page) ? parseInt(to.query.page) : 1;
      // let totalPage=parseInt(this.totalItem/8)+1;
      // val=parseInt(val%totalPage);
      // val=val===0?totalPage:val;
      this.currentPage = parseInt(to.query.page) ? parseInt(to.query.page) : 1;
      this.findIdleTiem(val);
    }
  },
  methods: {
    findIdleTiem: function findIdleTiem(page) {
      var _this = this;
      var loading = this.$loading({
        lock: true,
        text: '加载数据中',
        spinner: 'el-icon-loading',
        background: 'rgba(0, 0, 0, 0)'
      });
      if (this.labelName > 0) {
        this.$api.findIdleTiemByLable({
          idleLabel: this.labelName,
          page: page,
          nums: 8
        }).then(function (res) {
          console.log(res);
          var list = res.data.list;
          for (var i = 0; i < list.length; i++) {
            list[i].timeStr = list[i].releaseTime.substring(0, 10) + " " + list[i].releaseTime.substring(11, 19);
            var pictureList = JSON.parse(list[i].pictureList);
            list[i].imgUrl = pictureList.length > 0 ? pictureList[0] : '';
          }
          _this.idleList = list;
          _this.totalItem = res.data.count;
          console.log(_this.totalItem);
        }).catch(function (e) {
          console.log(e);
        }).finally(function () {
          loading.close();
        });
      } else {
        this.$api.findIdleTiem({
          page: page,
          nums: 8
        }).then(function (res) {
          console.log(res);
          var list = res.data.list;
          for (var i = 0; i < list.length; i++) {
            list[i].timeStr = list[i].releaseTime.substring(0, 10) + " " + list[i].releaseTime.substring(11, 19);
            var pictureList = JSON.parse(list[i].pictureList);
            list[i].imgUrl = pictureList.length > 0 ? pictureList[0] : '';
          }
          _this.idleList = list;
          _this.totalItem = res.data.count;
          console.log(_this.totalItem);
        }).catch(function (e) {
          console.log(e);
        }).finally(function () {
          loading.close();
        });
      }
    },
    handleClick: function handleClick(tab, event) {
      // console.log(tab,event);
      console.log(this.labelName);
      this.$router.replace({
        query: {
          page: 1,
          labelName: this.labelName
        }
      });
    },
    handleCurrentChange: function handleCurrentChange(val) {
      console.log("\u5F53\u524D\u9875: ".concat(val));
      this.$router.replace({
        query: {
          page: val,
          labelName: this.labelName
        }
      });
    },
    toDetails: function toDetails(idle) {
      this.$router.push({
        path: '/details',
        query: {
          id: idle.id
        }
      });
    }
  }
};
