import "core-js/modules/es6.regexp.replace";
import AppHead from "../common/AppHeader.vue";
import AppBody from "../common/AppPageBody.vue";
import AppFoot from "../common/AppFoot.vue";
import options from "../common/country-data.js";
import _upload from 'element-ui/packages/upload';
export default {
  name: "release",
  computed: {
    upload: function upload() {
      return _upload;
    }
  },
  components: {
    AppHead: AppHead,
    AppBody: AppBody,
    AppFoot: AppFoot
  },
  data: function data() {
    return {
      imgDialogVisible: false,
      dialogImageUrl: '',
      showFileList: true,
      options: options,
      selectedOptions: [],
      options2: [{
        value: 1,
        label: '数码科技'
      }, {
        value: 2,
        label: '生活用品'
      }, {
        value: 3,
        label: '运动相关'
      }, {
        value: 4,
        label: '图书笔记'
      }, {
        value: 5,
        label: '社区帖子'
      }],
      imgList: [],
      idleItemInfo: {
        idleName: '',
        idleDetails: '',
        pictureList: '',
        idlePrice: 0,
        idlePlace: '',
        idleLabel: ''
      }
    };
  },
  methods: {
    handleChange: function handleChange(value) {
      console.log(value);
      this.idleItemInfo.idlePlace = value[1];
    },
    imgChange: function imgChange(file, fileList) {
      this.noneBtnImg = fileList.length >= this.limitCountImg;
    },
    fileHandleRemove: function fileHandleRemove(file, fileList) {
      console.log(file, fileList);
      for (var i = 0; i < this.imgList.length; i++) {
        if (this.imgList[i] === file.response.data) {
          this.imgList.splice(i, 1);
        }
      }
    },
    fileHandlePreview: function fileHandlePreview(file) {
      console.log(file);
      this.dialogImageUrl = file.response.data;
      this.imgDialogVisible = true;
    },
    fileHandleSuccess: function fileHandleSuccess(response, file, fileList) {
      console.log("file:", response, file, fileList);
      this.imgList.push(response.data);
    },
    releaseButton: function releaseButton() {
      var _this = this;
      this.idleItemInfo.pictureList = JSON.stringify(this.imgList);
      console.log(this.idleItemInfo);
      if (this.idleItemInfo.idleName && this.idleItemInfo.idleDetails && this.idleItemInfo.idlePlace && this.idleItemInfo.idleLabel && (this.idleItemInfo.idlePrice || this.idleItemInfo.idleLabel === 5)) {
        this.$api.addIdleItem(this.idleItemInfo).then(function (res) {
          if (res.status_code === 1) {
            _this.$message({
              message: '发布成功！',
              type: 'success'
            });
            console.log(res.data);
            _this.$router.replace({
              path: '/details',
              query: {
                id: res.data.id
              }
            });
          } else {
            _this.$message.error('发布失败！' + res.msg);
          }
        }).catch(function (e) {
          _this.$message.error('请填写完整信息');
        });
      } else {
        this.$message.error('请填写完整信息！');
      }
    },
    handleExceed: function handleExceed(files, fileList) {
      this.$message.warning("\u9650\u523610\u5F20\u56FE\u7247\uFF0C\u672C\u6B21\u9009\u62E9\u4E86 ".concat(files.length, " \u5F20\u56FE\uFF0C\u5171\u9009\u62E9\u4E86 ").concat(files.length + fileList.length, " \u5F20\u56FE"));
    },
    isNotice: function isNotice() {}
  }
};
