.header {
	position: fixed;
	top: 0;
	left: 0;
	right: 0;
	min-width: 100vw;
	height: 58px;
	background: #ffffff;
	display: flex;
	justify-content: space-between;
	align-items: center;
	border-bottom: #eeeeee solid 2px;
	z-index: 1000;
}

.app-name {
	display: flex;
	justify-content: center;
	align-items: center;
	min-width: 10vw;
	flex: 1;
	height: 100%;
	border-right: 1px solid #e5e5e5;
}

.app-name a {
	color: #409EFF;
	font-size: 18px;
	font-weight: 800;
	text-decoration: none;
	padding: 0 20px;
}

.app-title {
	display: flex;
	justify-content: center;
	flex: 8;
	width: 100px;
}

.app-logOut {
	display: flex;
	flex: 1;
	justify-content: flex-end;
	align-items: center;
}

.mainBody {
	display: flex;
	width: 100%;
}

aside {
	flex: 1;
	box-sizing: content-box;
	min-width: 10vw;
	min-height: calc(100vh - 120px);
	background-color: rgb(255, 255, 255);
	border-bottom: 1px solid #e5e5e5;
	border-right: 1px solid #e5e5e5;
}

main {
	flex: 9;
}

.foot {
	position: absolute;
	left: 0;
	bottom: 0;
	width: 100%;
	height: 58px;
	background-color: #ffffff;
}
