.release-idle-container {
    min-height: 85vh;
}

.release-idle-container-title {
    font-size: 18px;
    padding: 30px 0;
    font-weight: 600;
    width: 100%;
    text-align: center;
}

.release-idle-container-form {
    padding: 0 180px;
}

.release-idle-detiles-text {
    margin: 20px 0;
}
.release-idle-place{
    margin-bottom: 15px;
}
.release-tip{
    color: #555555;
    float: left;
    padding-right: 5px;
    height: 36px;
    line-height: 36px;
    font-size: 14px;
}
.release-idle-container-picture{
    margin: 20px 0;

}
.release-idle-container-picture-title{
    margin: 10px 0;
    color: #555555;
    font-size: 14px;
    display: none;
}
.picture-list {
    margin: 20px 0;
    display: flex;
    flex-direction: column;
    align-items: center;
}

