result:
.page-wrapper {
    min-height: 100vh;
}

.home-container {
    padding: var(--el-spacing-xl);
    min-height: 85vh;
}

.home-header {
    margin-bottom: var(--el-spacing-xl);
    text-align: center;
}

.main-title {
    font-size: 32px;
    color: var(--el-color-primary);
    margin-bottom: var(--el-spacing-sm);
}

.sub-title {
    font-size: var(--el-font-size-medium);
    color: var(--el-text-color-secondary);
}

.category-tabs {
    margin-bottom: var(--el-spacing-xl);
}

.product-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
    gap: var(--el-spacing-lg);
    margin-bottom: var(--el-spacing-xl);
}

.product-card {
    height: 100%;
    cursor: pointer;
    transition: transform 0.3s;
}

.product-card:hover {
    transform: translateY(-5px);
}

.product-image {
    position: relative;
    height: 200px;
    overflow: hidden;
}

.product-image .el-image {
    width: 100%;
    height: 100%;
}

.product-tag {
    position: absolute;
    top: 10px;
    right: 10px;
    background-color: var(--el-color-success);
    color: white;
    padding: 2px 8px;
    border-radius: var(--el-border-radius-small);
    font-size: var(--el-font-size-extra-small);
}

.product-tag.sold {
    background-color: var(--el-color-info);
}

.product-info {
    padding: var(--el-spacing-md);
}

.product-title {
    font-size: var(--el-font-size-medium);
    margin-bottom: var(--el-spacing-sm);
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
}

.product-meta {
    display: flex;
    justify-content: space-between;
    margin-bottom: var(--el-spacing-xs);
}

.product-price {
    color: var(--el-color-danger);
    font-weight: 500;
    font-size: var(--el-font-size-medium);
}

.product-location {
    color: var(--el-text-color-secondary);
    font-size: var(--el-font-size-small);
}

.product-time {
    color: var(--el-text-color-secondary);
    font-size: var(--el-font-size-extra-small);
    margin-bottom: var(--el-spacing-sm);
}

.product-user {
    display: flex;
    align-items: center;
}

.user-name {
    margin-left: var(--el-spacing-sm);
    font-size: var(--el-font-size-small);
    color: var(--el-text-color-regular);
}

.pagination-container {
    display: flex;
    justify-content: center;
    padding: var(--el-spacing-lg) 0;
}

img{
    width: 100%;
    height: 100%;
    object-fit: fill;
}
/*走马灯 css*/
.el-carousel__item h3 {
    color: #475669;
    font-size: 14px;
    opacity: 0.75;
    line-height: 200px;
    margin: 0;

}

.el-carousel__item:nth-child(2n) {
    background-color: #99a9bf;
}

.el-carousel__item:nth-child(2n+1) {
    background-color: #d3dce6;
}



.idle-card {
    height: 300px;
    border: #eeeeee solid 1px;
    margin-bottom: 15px;
    cursor: pointer;
}

.fenye {
    display: flex;
    justify-content: center;
    height: 60px;
    align-items: center;
}

.idle-title {
    font-size: 18px;
    font-weight: 600;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
    margin: 10px;
}

.idle-prive {
    font-size: 16px;
    color: red;
}

.idle-place {
    font-size: 13px;
    color: #666666;
    float: right;
    padding-right: 20px;

}

.idle-time {
    color: #666666;
    font-size: 12px;
    margin: 0 10px;
}

.user-nickname {
    color: #999999;
    font-size: 12px;
    display: flex;
    align-items: center;
    height: 30px;
    padding-left: 10px;
}

.user-info {
    padding: 5px 10px;
    height: 30px;
    display: flex;
}
